import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

export default class TransactionsList extends Component {

    constructor(props) {
        super(props);
        this.deleteTransaction = this.deleteTransaction.bind(this);
        this.state = {transactions: []};
      }

      componentDidMount() {
        axios.get('http://localhost:5000/transactions/')
         .then(response => {
           this.setState({ transactions: response.data });
         })
         .catch((error) => {
            console.log(error);
         })
      }

      deleteTransaction(id) {
        axios.delete('http://localhost:5000/transactions/'+id)
          .then(res => console.log(res.data));
        this.setState({
          deleteTransaction: this.state.transactions.filter(el => el._id !== id)
        })
      }

      transactionList() {
        return this.state.transactions.map(currenttransaction => {
          return <transaction transaction={currenttransaction} deleteExercise={this.deleteTransaction} key={currenttransaction._id}/>;
        })
      }

  render() {
    return (
        <div>
        <h3>Logged Transactions</h3>
        <table className="table">
          <thead className="thead-light">
            <tr>
              <th>Username</th>
              <th>Role</th>
              <th>Amount</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            { this.transactionList() }
          </tbody>
        </table>
      </div>
    )
  }
}
